+    println!("FD: {:?}", fd);
+    let mut pid = unsafe { libc::tcgetpgrp(fd) };
+    println!("PID1: {:?}", pid);
     if pid < 0 {
         pid = tty::child_pid();
     }
+    println!("PID2: {:?}", pid);

     #[cfg(not(any(target_os = "macos", target_os = "freebsd")))]
     let link_path = format!("/proc/{}/cwd", pid);

