```diff
diff --git a/alacritty/src/display/mod.rs b/alacritty/src/display/mod.rs
index 91f575e4..92d31e34 100644
--- a/alacritty/src/display/mod.rs
+++ b/alacritty/src/display/mod.rs
@@ -8,7 +8,7 @@ use std::{cmp, mem};
 
 use glutin::dpi::PhysicalSize;
 use glutin::event::ModifiersState;
-use glutin::event_loop::EventLoopWindowTarget;
+use glutin::event_loop::{EventLoopProxy, EventLoopWindowTarget};
 #[cfg(all(feature = "x11", not(any(target_os = "macos", windows))))]
 use glutin::platform::unix::EventLoopWindowTargetExtUnix;
 use glutin::window::CursorIcon;
@@ -45,10 +45,11 @@ use crate::display::damage::RenderDamageIterator;
 use crate::display::hint::{HintMatch, HintState};
 use crate::display::meter::Meter;
 use crate::display::window::Window;
-use crate::event::{Mouse, SearchState};
+use crate::event::{EventType, Mouse, SearchState};
 use crate::message_bar::{MessageBuffer, MessageType};
 use crate::renderer::rects::{RenderLines, RenderRect};
 use crate::renderer::{self, GlyphCache, Renderer};
+use crate::Event;
 
 pub mod content;
 pub mod cursor;
@@ -694,6 +695,7 @@ impl Display {
     pub fn draw<T: EventListener>(
         &mut self,
         mut terminal: MutexGuard<'_, Term<T>>,
+        event_proxy: &EventLoopProxy<Event>,
         message_buffer: &MessageBuffer,
         config: &UiConfig,
         search_state: &SearchState,
@@ -861,7 +863,7 @@ impl Display {
         // Frame event should be requested before swaping buffers, since it requires surface
         // `commit`, which is done by swap buffers under the hood.
         #[cfg(all(feature = "wayland", not(any(target_os = "macos", windows))))]
-        self.request_frame(&self.window);
+        self.request_frame(&self.window, event_proxy);
 
         // Clearing debug highlights from the previous frame requires full redraw.
         if self.is_damage_supported && !self.debug_damage {
@@ -1094,20 +1096,26 @@ impl Display {
     /// Requst a new frame for a window on Wayland.
     #[inline]
     #[cfg(all(feature = "wayland", not(any(target_os = "macos", windows))))]
-    fn request_frame(&self, window: &Window) {
+    fn request_frame(&self, window: &Window, event_proxy: &EventLoopProxy<Event>) {
         let surface = match window.wayland_surface() {
             Some(surface) => surface,
             None => return,
         };
 
         let should_draw = self.window.should_draw.clone();
+        let skipped_draw = self.window.skipped_draw.clone();
 
         // Mark that window was drawn.
         should_draw.store(false, Ordering::Relaxed);
 
         // Request a new frame.
+        let proxy = event_proxy.clone();
+        let id = window.id();
         surface.frame().quick_assign(move |_, _, _| {
             should_draw.store(true, Ordering::Relaxed);
+            if skipped_draw.load(Ordering::Relaxed) {
+                let _ = proxy.send_event(Event::new(EventType::Redraw, id));
+            }
         });
     }
 }
diff --git a/alacritty/src/display/window.rs b/alacritty/src/display/window.rs
