             wayland_surface,
             scale_factor,
diff --git a/alacritty/src/event.rs b/alacritty/src/event.rs
index d16114af..c9427a50 100644
--- a/alacritty/src/event.rs
+++ b/alacritty/src/event.rs
@@ -91,6 +91,7 @@ pub enum EventType {
     CreateWindow(WindowOptions),
     BlinkCursor,
     SearchNext,
+    Redraw,
 }
 
 impl From<TerminalEvent> for EventType {
@@ -1107,6 +1108,9 @@ impl input::Processor<EventProxy, ActionContext<'_, Notifier, EventProxy>> {
                     TerminalEvent::CursorBlinkingChange => self.ctx.update_cursor_blinking(),
                 },
                 EventType::ConfigReload(_) | EventType::CreateWindow(_) => (),
+                EventType::Redraw => {
+                    *self.ctx.dirty = true
+                },
             },
             GlutinEvent::RedrawRequested(_) => *self.ctx.dirty = true,
             GlutinEvent::WindowEvent { event, .. } => {
diff --git a/alacritty/src/window_context.rs b/alacritty/src/window_context.rs
index 6ead6e4e..ca7a3c14 100644
--- a/alacritty/src/window_context.rs
+++ b/alacritty/src/window_context.rs
@@ -321,8 +321,10 @@ impl WindowContext {
         // Skip rendering on Wayland until we get frame event from compositor.
         #[cfg(not(any(target_os = "macos", windows)))]
         if !self.display.is_x11 && !self.display.window.should_draw.load(Ordering::Relaxed) {
+            self.display.window.skipped_draw.store(self.dirty, Ordering::Relaxed);
             return;
         }
+        self.display.window.skipped_draw.store(false, Ordering::Relaxed);
 
         if self.dirty {
             self.dirty = false;
@@ -333,7 +335,13 @@ impl WindowContext {
             }
 
             // Redraw screen.
-            self.display.draw(terminal, &self.message_buffer, config, &self.search_state);
+            self.display.draw(
+                terminal,
+                event_proxy,
+                &self.message_buffer,
+                config,
+                &self.search_state,
+            );
         }
     }
 
