Transcribed from this thread:
https://groups.google.com/forum/#!msg/closure-compiler-discuss/1hPmS7EOhj8/v0AKzqZ0-lIJ

//------------------
//app.js

var Hello = require('./Hello.js');

var hello = new Hello('world');
hello.sayHello('Adolf');

//------------------
//Hello.js

/**
  @constructor
  @param {string} name
*/
function Hello(name) {
  this.name = name;
}

/**
  @param {string} greeter
*/
Hello.prototype.sayHello = function(greeter) {
  return console.log(&quot;Hello &quot; + this.name + &quot; from &quot; + greeter + &quot;!&quot;);
};

module.exports = Hello;

//------------------

java -jar compiler.jar --js app.js --js Hello.js --externs externs.js --js_output_file all.js --process_common_js_modules --common_js_entry_module app.js --compilation_level ADVANCED_OPTIMIZATIONS --formatting=pretty_print --warning_level VERBOSE

should emit no warnings.
